awk '/Setup mode/,/Hold mode/ { if (/WNS \(ns\):/ && !s) { match($0, /([+-]?[0-9]+\.?[0-9]*)/, a); setup=a[1]; s=1 } } /Hold mode/,/^$/ { if (/WNS \(ns\):/ && !h) { match($0, /([+-]?[0-9]+\.?[0-9]*)/, a); hold=a[1]; h=1 } } END { printf "Setup: %s (%.3f ns), Hold: %s (%.3f ns), Overall: %s\n", (setup>=0?"PASS":"FAIL"), setup, (hold>=0?"PASS":"FAIL"), hold, (setup>=0 && hold>=0?"PASS":"FAIL") }' timing_report/picorv32.summary
